export default class MainHeader extends React.Component<{ location: any }> {
  
  state = {
    modal: false
  }

  toggleModal = () => {
    this.setState({
      modal: !this.state.modal
    })
  }
  
  render() {
    const { location } = this.props

    return (
      <StaticQuery
        query={graphql`
          query HeaderTitleQuery {
            site {  
              siteMetadata {
                title
              }
            }
          }`
        }
          render={data => (
            <section>
              <Helmet
                title={data.site.siteMetadata.title}
                meta={[
                  { name: 'description', content: 'Sample' },
                  { name: 'keywords', content: 'sample, something' },
                ]}
              />
              <Header style={{position: 'fixed', zIndex: 100000, width: '100%'}}>
                <Row>
                  <Col
                    sm={{
                      offset: 0,
                    }}
                    md={{
                      offset: 0,
                    }}
                    lg={{
                      offset: 1,
                    }}
                    xl={{
                      offset: 1,
                    }}
                    xxl={{
                      offset: 1
                    }}
                  >
                    <Media
                      query="(max-width: 576px)"
                    >
                      {(matches: any) => 
                      <Scrollchor to="" style={{textDecoration: 'none'}}>
                        <Link
                          to="/"
                          style={{
                            color: 'white',
                            fontWeight: 400,
                            fontFamily: 'Gill Sans, Lato',
                            letterSpacing: matches ? '1.2px' : '2px',
                            fontSize: matches ? 28 : 44,
                            lineHeight: '62px',
                            textDecoration: 'none',
                            width: matches ? 160 : 260,
                            float: 'left',
                          }}
                        >
                          <img src={Logo as unknown as string} style={{ height: matches ? '32px' : '48px', paddingRight: matches ? '8px' : '16px', marginBottom: '12px' }} />
                          <span style={{ color: '#06A9F4' }}>{data.site.siteMetadata.title[0]}</span>{data.site.siteMetadata.title.slice(1)}
                        </Link>
                      </Scrollchor>
                      }
                    </Media>
                  </Col>
                  <Col
                    xs={{
                      offset: 20
                    }}
                    sm={{
                      offset: 20,
                    }}
                    md={{
                      offset: 20,
                    }}
                    lg={{
                      offset: 22,
                    }}
                    xl={{
                      offset: 12,
                    }}
                    xxl={{
                      offset: 15,
                    }}
                  >
                    <Media
                      query="(max-width: 1223px)"
                    >
                    {(matches: any) => 
                      matches ?
                        <Button type="primary" ghost={true} icon="menu" style={brandButtonStyle} onClick={() => this.toggleModal()}>Menu</Button> :
                        <HeaderMenu location={location} mode={matches}/>
                    }
                    </Media>
                  </Col>
                </Row>
              </Header>
              <Modal
                visible={this.state.modal}
                onCancel={() => this.toggleModal()}
                style={{
                  left: 0,
                  top: 0,
                  minHeight: '100vh',
                  minWidth: '100vw',
                  margin: 0,
                }}
                bodyStyle={{
                  padding: 0,
                  width: '100vw',
                  height: '100vh',
                  background: brandDarkBlue,
                  position: 'absolute' as PositionProperty,
                  display: 'flex',
                  justifyContent: 'center',
                  // alignItems: 'center',
                  margin: 0,
                  top: 0,
                }}
                footer={null}
                keyboard={true}
                cancelButtonProps={{shape:'circle', type:'primary', icon:'close'}}
              >
                <div
                  style={{
                    position: 'absolute' as PositionProperty, 
                    color: 'white', 
                    alignSelf: 'center', 
                    display: 'block', 
                    width: '90%', 
                    marginLeft: 'auto', 
                    marginRight: 'auto', 
                    paddingLeft: '2%', 
                    paddingRight: '2%',
                    top: 40, 
                    height: '100%'
                  }}
                >
                  <Col
                    xs={{ span: 24 }}
                    sm={{ span: 20 }}
                    md={{ span: 16 }}
                    lg={{ span: 10 }}
                  >
                    <Row>
                      <Link
                        to="/"
                        style={{
                          color: 'white',
                          fontWeight: 400,
                          fontFamily: 'Gill Sans, Lato',
                          letterSpacing: '2px',
                          fontSize: 44,
                          lineHeight: '62px',
                          textDecoration: 'none',
                          width: 260,
                          float: 'left',
                        }}
                      >
                        <img src={Logo as unknown as string} style={{ height: '48px', paddingRight: '16px', marginBottom: '12px' }} />
                        <span style={{ color: '#06A9F4' }}>{data.site.siteMetadata.title[0]}</span>{data.site.siteMetadata.title.slice(1)}
                      </Link>
                    </Row>
                    <Row>
                      <HeaderMenu location={location} mode={true}/>
                    </Row>
                  </Col>
                </div>
              </Modal>
          </section>)
        } />
      )
    }
  }
